import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-ngfor',
  templateUrl: './ngfor.component.html',
  styleUrls: ['./ngfor.component.css']
})
export class NgforComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

  products = [
    {proimg:'data:image/jpeg;base64,',proname:'Laptop',proid:'prod01',proprice:15000},
    {proimg:'data:image/jpeg;base64,',proname:'Mobile',proid:'prod02',proprice:10000},
    {proimg:'data:image/jpeg;base64,',proname:'Washing machine',proid:'prod03',proprice:20000},
    {proimg:'data:image/jpeg;base64,',proname:'TV',proid:'prod04',proprice:12000}
  ]

  users = [];

  createUser(uname){
    this.users.push({
    name:uname.value
    })
  }

  removeUser(){
    this.users.splice(this.users.length-1);
  }

  removeItem(item){
    this.users.splice(item,1);
  }
}
